#!/usr/bin/env bash

KAFKA_VERSION=2.0.0
CF_VERSION=5.0.0

SERVICES=(
    "cd"
    "zookeeper"
    "kafka"
    "kafka-cli"
    "kafka-topics"
    "schema-registry"
    "grafana"
    "prometheus"
)

HOST1=172.17.0.2

#Zookeeper
ZK_SERVERS=($HOST1)
ZOOKEEPER_PORT=2181

#Schema Registry
SCHEMA_REGISTRY_HOST=$HOST1
SCHEMA_REGISTRY_PORT=8082
AVRO_COMPATIBILITY_LEVEL=full_transitive

#Kafka Broker
KAFKA_SERVERS=($HOST1)
KAFKA_ADVERTISED_HOSTS=(localhost)
KAFKA_LOG_DIRS="/data/kafka"
KAFKA_PROTOCOL="PLAINTEXT"
KAFKA_REPL_FACTOR=1
KAFKA_PORT=9092
KAFKA_JMX_PORT=29092
KAFKA_JMX_PROMETHEUS_PORT=7071
#KAFKA_SASL_MECHANISM=PLAIN
#KAFKA_AUTHORIZER_CLASS_NAME=kafka.security.auth.SimpleAclAuthorizer

#Metrics
GRAFANA_SERVERS=($HOST1)
PROMETHEUS_SERVERS=($HOST1)
PROMETHEUS_DATA_DIR=/data/prometheus/
PROMETHEUS_RETENTION=7d
